import React from "react";
import './App.css';

function Form() {
  const handleSubmit=event=>{
    event.preventDefault();
    let userNick=document.getElementById("userNick");
    let userEmail=document.getElementById("userEmail");
    let userSex=document.getElementById("userSex");
    let userAge=document.getElementById("userAge");
    alert(userNick.value + userEmail.value + userSex.value + userAge.value);
  };
  return(
    <form onSubmit={handleSubmit}>
      <input type="text" id="userNick" placeholder="Nick-Name"/>
      <br/>
      <input type="text" id="userEmail" placeholder="Email"/>
      <br/>
      <input type="text" id="userSex" placeholder="Sex"/>
      <br/>
      <input type="text" id="userAge" placeholder="Age"/>
      <br/>
      <input type="submit" value="Register"/>
    </form>
  );
}

export default function App(){
  return(
    <>
    <Form/>
    </>
  );
}
